import React from 'react';
import "./contact.css";
import email from "../../img/email.png";
import phone from "../../img/call.png";
import address from "../../img/address.png";
import { useRef } from "react";
import emailjs from 'emailjs-form';

const Contact = () => {
    const formRef = useRef()

    const handleSubmit = (e) => {
        e.preventDefault();
        emailjs.sendForm('service_0pp9inc', 'tempelate_b2sa5lr', formRef.current, 'user_Q3iOaKy6j5eWFXc3NaKiY')
            .then((result) => {
                console.log(result.text);
            }, (error) => {
                console.log(error.text);
            });

    }

    return (
        <div className="c">
            <div className="c-bg"></div>
            <div className="c-wrapper">
                <div className="c-left">
                    <h1 className="c-title">Let's discuss your project</h1>
                    <div className="c-info">
                        <div className="c-info-item">
                            <img src={phone} alt="" className="c-icon" />
                            +977987654321
                        </div>
                        <div className="c-info-item">
                            <img src={email} alt="" className="c-icon" />
                            avi.shrestha.775@gmail.com
                        </div>
                        <div className="c-info-item">
                            <img src={address} alt="" className="c-icon" />
                            18 Balaju, Kathmandu 44600 Nepal
                        </div>
                    </div>
                </div>
                <div className="c-right">
                    <p className="c-desc">
                        <b> What's your story?</b> Lorem ipsum dolor sit amet consectetur adipisicing elit. Dolorum illum illo assumenda non, corporis ad id consequatur? Totam earum labore eligendi esse obcaecati quaerat vel laboriosam autem odio. Exercitationem, beatae.
                    </p>
                    <form ref={formRef} onSubmit={handleSubmit}>
                        <input type="text" name="user_name" placeholder="Name" />
                        <input type="text" name="user_subject" placeholder="Subject" />
                        <input type="text" name="user_email" placeholder="Email" />
                        <textarea placeholder="Message" name="message" rows="5"></textarea>
                        <button>Submit</button>
                    </form>
                </div>
            </div>
        </div>
    )
};

export default Contact;
